import { io } from "socket.io-client";

const ConnectionURI = "http://192.168.9.228:8080";
const socket = io(ConnectionURI);

export async function userConnet(name) {
  socket.emit("new-user-connet", name);

  await socket.on("new-user-connet", (user) =>
    console.log("user " + user + " joined the chat !")
  );
}

export const sendMessage = (message, user) => {
  socket.emit("message", { message, user });
};
