import React, { useEffect, useState } from "react";
import { View, StyleSheet, Text, TextInput, Alert, Button } from "react-native";
import { sendMessage, userConnet, socket } from "../api/httpService";
import AppButton from "../components/AppButton";
import MessegeCard from "../components/MessegeCard";
import AppTextInput from "./../components/AppTextInput";

function HomeScreen({}) {
  const [mesInput, setMessageInput] = useState("");
  const [messages, setMasseges] = useState([]);
  const [user, setUser] = useState("");
  const [dilog, setdilog] = useState(true);

  useEffect(() => {
    userConnet(user);
  }, []);

  socket.on("connection", (listen) => {
    listen.on("message", (m) => console.log("message recived " + message));
  });

  return (
    <View style={styles.container}>
      <AppTextInput
        placeholder="message"
        value={mesInput}
        onChangeText={setMessageInput}
      />
      <AppButton title="Send" onPress={() => sendMessage(mesInput, "Naval")} />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    margin: 30,
    justifyContent: "flex-end",
    backgroundColor: "#fff",
    flex: 1,
  },
});

export default HomeScreen;
