import { io } from "socket.io-client";

const ConnectionURI = "http://192.168.9.228:8080";
export const socket = io(ConnectionURI);

export async function userConnet(name) {
  socket.emit("new-user-connet", name);
}

export const sendMessage = (message, user) => {
  socket.emit("message-send", { message, user });
};
