import React, { useEffect, useState } from "react";
import { View, StyleSheet, Text, TextInput, Alert, Button } from "react-native";
import prompt from "react-native-prompt-android";
import { connect } from "../api/httpService";
import MessegeCard from "../components/MessegeCard";

function HomeScreen({}) {
  const [mesInput, setMessageInput] = useState("");
  const [messages, setMasseges] = useState([]);
  const [user, setUser] = useState("");

  useEffect(() => {
    promptName();
    connect(user);
  }, []);

  const promptName = () => {
    Alert.prompt("Enter Nickname", "Enter your to join the chat", [
      {
        text: "OK",
        onPress: (name) => setUser(name),
      },
    ]);
  };

  return (
    <View style={styles.container}>
      {messages.map((mesege) => (
        <MessegeCard mes={mesege} />
      ))}
      <Text></Text>
      <Button title="prompt" onPress={promptName} />
      <TextInput
        underlineColorAndroid="black"
        value={mesInput}
        onChangeText={setMessageInput}
      />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    backgroundColor: "#f3f3",
    flex: 1,
    flexDirection: "column-reverse",
  },
});

export default HomeScreen;
